import React, { Component } from 'react'
import AppContext, { AppProvider } from '../../../AppContext'
import styles from './ChatHistory.module.scss'

export default class ChatHistory extends Component {

    getUsernameStyle = (index) => {
        return index % 2 === 0 ?
            styles.userMessage
        :   styles.systemMessage
    }

    getObjectString = (index) => {
        return this.context.mockHistory.messages[element][index];
    }

    getChatHistory = () => {
        return Object.keys(this.context.mockHistory.messages).map((element, index) =>
            <div className={styles.message} key={index}>
                <div className={this.getUsernameStyle(index)}>{this.getObjectString(0)}</div>
                <div>{this.getObjectString(1)}</div>
            </div>
        );
    }

    static contextType = AppContext;

    render() {
        return (
            <AppProvider>
                <div className={styles.chatHistory}>
                    {this.getChatHistory()}
                </div>
            </AppProvider>
        )
    }
}
