import React, { Component } from 'react'

const AppContext = React.createContext();

export class AppProvider extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username: 'Alex',

            currentPrompt: '',
            isPromptSubmitted: false,

            mockHistory: {
                messages: {
                    message0: ["Fred", "Hi! How can I help?"]
                }
            }

        }
    }

    getChatHistory = () => {
        return Object.keys(this.context.mockHistory.messages).map((element, index) =>
            <div className={styles.message} key={index}>
                <div className={this.getUsernameStyle(index)}>{this.getObjectString(element, 0)}</div>
                <div>{this.getObjectString(element, 1)}</div>
            </div>
        );
    }

    render() {
        return (
            <AppContext.Provider value={{
                ...this.state,
                ...this
            }}>
                {this.props.children}
            </AppContext.Provider>
        )
    }
}

export default AppContext;
