import React, { Component } from 'react'
import AppContext, { AppProvider } from '../../../AppContext'
import styles from './ChatHistory.module.scss'

export default class ChatHistory extends Component {

    /**
     * 
     * @param {*} index gets the correct color acco 
     * @returns 
     */
    getUsernameStyle = (element) => {
        this.context.systemUsernames.map((name, index) => {
            console.log("first", this.context.mockHistory.messages[element][0]);
            console.log("second", name);
            if (this.context.mockHistory.messages[element][0].includes(name)) {
                return styles.systemMessage
            }
        })
        return styles.userMessage;
    }

    getObjectString = (element, index) => {
        return this.context.mockHistory.messages[element][index];
    }

    getChatHistory = () => {
        return Object.keys(this.context.mockHistory.messages).map((element, index) =>
            <div className={styles.message} key={index}>
                <div className={this.getUsernameStyle(element)}>{this.getObjectString(element, 0)}</div>
                <div>{this.getObjectString(element, 1)}</div>
            </div>
        );
    }

    static contextType = AppContext;

    render() {
        return (
            <AppProvider>
                <div className={styles.chatHistory}>
                    {this.getChatHistory()}
                </div>
            </AppProvider>
        )
    }
}
