import React, { Component, Fragment } from 'react'
import styles from './CurrentMessage.module.scss'
import AppContext from '../../AppContext';

export default class CurrentMessage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPrompt: '',
            isPromptSubmitted: false
        };
    }

    static contextType = AppContext;

    handleChange = (event) => {
        this.setState({ currentPrompt: event.target.value });
    }

    handleSubmit = (event) => {
        alert('A message was submitted: ' + this.state.currentPrompt);
        event.preventDefault();
        console.log(this.props);
        this.setState({ 
            currentPrompt: '',
            isPromptSubmitted: true 
        });

        setTimeout(() => {
            this.setState({ 
                isPromptSubmitted: false 
            }); 
        }, 2000);

        //TODO : add load animation instead of the form
    }

    render() {
        console.log("aaa", this.context);
        return (
            <Fragment>
                {!this.state.isPromptSubmitted ?
                    <form onSubmit={this.handleSubmit}>
                        <label className={styles.userInput}>
                            <div className={styles.username}>User: </div>
                            <input className={styles.textArea} onChange={this.handleChange}></input>
                        </label>
                        <input type="submit" hidden />
                    </form>
                    
                    :

                    <div>Loading</div>
                }
            </Fragment>
        )
    }
}
